mapper.update:
update pet
set pname = #{pname,jdbcType=VARCHAR},
  uid = #{uid,jdbcType=INTEGER},
  col = #{col,jdbcType=INTEGER}
where pid = #{pid,jdbcType=INTEGER}

mapper.select:
select pid, pname, uid, col,url
from pet
where pid = #{pid,jdbcType=INTEGER}

